INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('151','Classic Margherita Pizza','classic-margherita-pizza','Traditional pizza with fresh mozzarella, basil, and tomato sauce.','pizzalover@example.com','{\"1\": \"Salt: 1/2 teaspoon\", \"2\": \"Onion: 1 small, thinly sliced (optional)\", \"3\": \"Olive Oil: 1 tablespoon\", \"4\": \"Bell Pepper: 1 small, sliced (optional)\", \"5\": \"Fresh Basil: a few leaves\", \"6\": \"Pizza Dough: 1 ball (about 250g)\", \"7\": \"Tomato Sauce: 1/2 cup\", \"8\": \"Dried Oregano: 1 teaspoon\", \"9\": \"Pepperoni Slices: optional, 20 slices\", \"10\": \"Mozzarella Cheese: 1 cup, shredded\"}','{\"1\": \"Preheat oven to 220°C (430°F).\", \"2\": \"Roll out the pizza dough on a floured surface to your desired thickness.\", \"3\": \"Transfer the dough to a baking sheet or pizza stone.\", \"4\": \"Spread tomato sauce evenly over the dough.\", \"5\": \"Sprinkle mozzarella cheese over the sauce.\", \"6\": \"Add toppings like pepperoni, bell pepper, and onion if using.\", \"7\": \"Drizzle olive oil over the pizza and sprinkle salt and oregano.\", \"8\": \"Bake for 12-15 minutes, or until the crust is golden and cheese is bubbly.\", \"9\": \"Remove from oven, add fresh basil leaves on top, slice, and serve.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('152','Coconut Curry Lentils','coconut-curry-lentils','Creamy lentils cooked in a fragrant coconut curry sauce.','veganchef@example.com','{\"Salt\": \"to taste\", \"Olive oil\": \"2 tbsp\", \"Lime juice\": \"1 tbsp (optional)\", \"Black pepper\": \"to taste\", \"Coconut milk\": \"400 ml (1 can)\", \"Curry powder\": \"1.5 tbsp\", \"Tomato paste\": \"2 tbsp\", \"Garlic (minced)\": \"3 cloves\", \"Ginger (grated)\": \"1 tbsp\", \"Ground turmeric\": \"1 tsp\", \"Cooked rice or naan\": \"to serve\", \"Red lentils (rinsed)\": \"1 cup\", \"Onion (finely chopped)\": \"1 medium\", \"Fresh cilantro (chopped)\": \"for garnish\", \"Vegetable broth or water\": \"2 cups\"}','{\"1\": \"Heat olive oil in a large saucepan over medium heat.\", \"2\": \"Add chopped onion and sauté for 5-7 minutes until soft and translucent.\", \"3\": \"Stir in garlic and ginger, cooking for another 1-2 minutes until fragrant.\", \"4\": \"Add curry powder and turmeric, and cook for 30 seconds to bloom the spices.\", \"5\": \"Add rinsed red lentils, coconut milk, vegetable broth (or water), and tomato paste. Stir to combine.\", \"6\": \"Bring to a boil, then reduce heat and simmer uncovered for 20-25 minutes, or until lentils are soft and the curry is thickened.\", \"7\": \"Season with salt and pepper to taste. Stir in lime juice if using.\", \"8\": \"Serve hot, garnished with chopped cilantro, alongside cooked rice or naan.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('153','Buffalo Wings','buffalo-wings','Spicy fried chicken wings tossed in buffalo sauce.','wingmaster@example.com','{\"Salt\": \"1 teaspoon\", \"Honey\": \"1 tablespoon (optional, for a mild sweetness)\", \"Butter\": \"1/2 cup (about 115g)\", \"Vinegar\": \"1 tablespoon (optional)\", \"Hot Sauce\": \"1/2 cup (e.g., Frank’s RedHot)\", \"Black Pepper\": \"1/2 teaspoon\", \"Baking Powder\": \"1 tablespoon\", \"Chicken Wings\": \"1 kg\", \"Garlic Powder\": \"1 teaspoon\", \"Oil for Frying\": \"as needed\"}','{\"1\": \"Pat the chicken wings dry with paper towels.\", \"2\": \"In a bowl, toss wings with salt, pepper, garlic powder, and baking powder until evenly coated.\", \"3\": \"Preheat oven to 220°C (425°F) or prepare oil for deep frying.\", \"4\": \"Bake wings on a wire rack over a baking sheet for 45–50 minutes, flipping halfway, or fry them until golden and crispy.\", \"5\": \"While wings cook, melt butter in a saucepan over low heat. Add hot sauce, vinegar, and honey if using. Stir until combined.\", \"6\": \"Toss cooked wings in the buffalo sauce until well coated.\", \"7\": \"Serve hot with celery sticks and blue cheese or ranch dressing.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('154','Greek Moussaka','greek-moussaka','Layered eggplant and ground meat casserole topped with béchamel.','mediterraneancook@example.com','{\"1\": \"Eggplants (sliced lengthwise into 1/2-inch thick slices): 2 large\", \"2\": \"Potatoes (sliced into 1/4-inch rounds): 2 large\", \"3\": \"Olive oil: for brushing and frying\", \"4\": \"Ground beef or lamb: 500 g (1.1 lbs)\", \"5\": \"Onion (chopped): 1 medium\", \"6\": \"Garlic (minced): 2 cloves\", \"7\": \"Tomato paste: 2 tbsp\", \"8\": \"Canned crushed tomatoes: 400 g (14 oz)\", \"9\": \"Red wine: 1/4 cup (optional)\", \"10\": \"Ground cinnamon: 1/2 tsp\", \"11\": \"Dried oregano: 1 tsp\", \"12\": \"Salt: to taste\", \"13\": \"Black pepper: to taste\", \"14\": \"Butter (for béchamel sauce): 4 tbsp\", \"15\": \"All-purpose flour (for béchamel sauce): 4 tbsp\", \"16\": \"Milk (warm, for béchamel sauce): 2 cups\", \"17\": \"Egg yolks (for béchamel sauce): 2\", \"18\": \"Grated Parmesan cheese: 1/2 cup\", \"19\": \"Nutmeg (optional, for béchamel sauce): a pinch\"}','{\"1\": \"Preheat oven to 200°C (390°F). Brush eggplant and potato slices with olive oil and roast or fry until tender and golden. Set aside.\", \"2\": \"In a large skillet, heat some olive oil and sauté chopped onion until translucent. Add garlic and cook for 1 minute.\", \"3\": \"Add ground beef or lamb and cook until browned. Drain excess fat if needed.\", \"4\": \"Stir in tomato paste, crushed tomatoes, red wine (if using), cinnamon, oregano, salt, and pepper. Simmer for 20 minutes until thickened.\", \"5\": \"In a saucepan, melt butter over medium heat. Whisk in flour to form a roux and cook for 1-2 minutes without browning.\", \"6\": \"Gradually whisk in warm milk. Cook and stir until smooth and thickened. Remove from heat.\", \"7\": \"Stir in egg yolks, grated Parmesan, and a pinch of nutmeg (if using). Mix well to complete the béchamel sauce.\", \"8\": \"In a baking dish, layer potatoes, then half the eggplant, followed by the meat sauce, the rest of the eggplant, and finally the béchamel sauce on top.\", \"9\": \"Bake at 180°C (350°F) for 40-45 minutes or until the top is golden and bubbling. Let it rest for 15–20 minutes before serving.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('155','Pesto Pasta','pesto-pasta','Pasta tossed in fresh basil pesto with pine nuts and parmesan.','italianfoodie@example.com','{\"Salt\": \"to taste\", \"Pasta\": \"400 grams (spaghetti, linguine, or your choice)\", \"Garlic\": \"2 cloves\", \"Olive Oil\": \"1/2 cup\", \"Pine Nuts\": \"1/4 cup\", \"Black Pepper\": \"to taste\", \"Cherry Tomatoes\": \"1 cup, halved (optional)\", \"Parmesan Cheese\": \"1/2 cup grated\", \"Fresh Basil Leaves\": \"2 cups packed\"}','{\"1\": \"Cook pasta according to package instructions until al dente. Drain and set aside.\", \"2\": \"In a food processor, combine basil leaves, garlic, pine nuts, and Parmesan cheese.\", \"3\": \"Pulse while slowly adding olive oil until smooth and creamy. Season with salt and pepper.\", \"4\": \"Toss the cooked pasta with the pesto sauce until evenly coated.\", \"5\": \"Add cherry tomatoes if desired and mix gently.\", \"6\": \"Serve immediately with extra Parmesan on top if you like.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('156','Shakshuka','shakshuka','Poached eggs in a spicy tomato and pepper sauce.','brunchlover@example.com','{\"Eggs\": \"4–6\", \"Salt\": \"to taste\", \"Paprika\": \"1 tsp\", \"Olive oil\": \"2 tbsp\", \"Black pepper\": \"to taste\", \"Ground cumin\": \"1 tsp\", \"Tomato paste\": \"1 tbsp\", \"Bread or pita\": \"for serving\", \"Garlic (minced)\": \"3 cloves\", \"Onion (chopped)\": \"1 medium\", \"Canned diced tomatoes\": \"1 can (400 g / 14 oz)\", \"Crumbled feta (optional)\": \"for garnish\", \"Red bell pepper (chopped)\": \"1\", \"Fresh parsley or cilantro (chopped)\": \"for garnish\", \"Ground chili or cayenne pepper (optional)\": \"1/4 tsp\"}','{\"1\": \"Heat olive oil in a large skillet over medium heat. Add chopped onion and bell pepper, and cook for 5–7 minutes until softened.\", \"2\": \"Add garlic, cumin, paprika, and chili (if using). Cook for 1 minute until fragrant.\", \"3\": \"Stir in diced tomatoes and tomato paste. Simmer for 10–15 minutes, stirring occasionally, until the sauce thickens.\", \"4\": \"Season with salt and black pepper to taste.\", \"5\": \"Make small wells in the sauce and crack eggs into them. Cover the skillet and cook for 5–7 minutes, or until egg whites are set and yolks are still runny (or to your liking).\", \"6\": \"Remove from heat. Garnish with chopped parsley or cilantro and crumbled feta if desired.\", \"7\": \"Serve hot with crusty bread or pita for dipping.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('157','Stuffed Bell Peppers','stuffed-bell-peppers','Bell peppers filled with rice, vegetables, and cheese.','comfortfood@example.com','{\"Salt\": \"to taste\", \"Olive oil\": \"2 tbsp\", \"Cooked rice\": \"1 cup\", \"Black pepper\": \"to taste\", \"Tomato sauce\": \"1 cup\", \"Dried oregano\": \"1 tsp\", \"Garlic (minced)\": \"2 cloves\", \"Onion (chopped)\": \"1 medium\", \"Ground beef or turkey\": \"500 g (1 lb)\", \"Fresh parsley (chopped)\": \"for garnish\", \"Shredded mozzarella or cheddar cheese\": \"1 cup\", \"Bell peppers (any color, tops cut off and seeds removed)\": \"4 large\"}','{\"1\": \"Preheat oven to 190°C (375°F). Lightly grease a baking dish.\", \"2\": \"Cut tops off the bell peppers and remove seeds and membranes. Place them in the baking dish, standing upright.\", \"3\": \"In a large skillet, heat olive oil over medium heat. Sauté onion for 3–4 minutes until soft. Add garlic and cook for 1 more minute.\", \"4\": \"Add ground beef or turkey. Cook until browned, breaking it apart with a spoon. Drain excess fat if needed.\", \"5\": \"Stir in cooked rice, tomato sauce, oregano, salt, and pepper. Simmer for 5 minutes until well combined.\", \"6\": \"Spoon the meat mixture into each pepper until full. Top with shredded cheese.\", \"7\": \"Cover with foil and bake for 30 minutes. Remove foil and bake for an additional 10–15 minutes until cheese is bubbly and golden.\", \"8\": \"Garnish with chopped parsley and serve hot.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('158','BBQ Ribs','bbq-ribs','Tender pork ribs coated with smoky barbecue sauce.','bbqking@example.com','{\"Salt\": \"1 tsp\", \"Paprika\": \"1 tbsp\", \"Brown sugar\": \"2 tbsp\", \"Black pepper\": \"1/2 tsp\", \"Onion powder\": \"1 tsp\", \"Aluminum foil\": \"for wrapping\", \"Garlic powder\": \"1 tsp\", \"Apple cider vinegar\": \"2 tbsp\", \"BBQ sauce (your favorite)\": \"1–1.5 cups\", \"Cayenne pepper (optional)\": \"1/4 tsp\", \"Pork ribs (baby back or spare ribs)\": \"1.5–2 kg (3–4 lbs)\"}','{\"1\": \"Preheat oven to 150°C (300°F). Remove the thin membrane from the back of the ribs.\", \"2\": \"In a bowl, mix brown sugar, paprika, garlic powder, onion powder, salt, pepper, and cayenne.\", \"3\": \"Rub the spice mix all over the ribs evenly.\", \"4\": \"Place the ribs meat side up on foil, drizzle with apple cider vinegar, and wrap tightly.\", \"5\": \"Place wrapped ribs on a baking sheet and bake for 2.5 to 3 hours until tender.\", \"6\": \"Preheat grill to medium-high (or oven broiler). Unwrap ribs and discard foil.\", \"7\": \"Brush BBQ sauce over ribs and grill or broil for 5–10 minutes, basting as desired.\", \"8\": \"Rest for a few minutes, slice between the bones, and serve with extra sauce.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('159','Falafel Salad','falafel-salad','Fresh salad topped with crispy chickpea falafel balls.','veganqueen@example.com','{\"1\": \"Salt: to taste\", \"2\": \"Tomato: 2 medium, diced\", \"3\": \"Falafel: 8-10 pieces, cooked\", \"4\": \"Cucumber: 1 medium, diced\", \"5\": \"Olive Oil: 3 tablespoons\", \"6\": \"Red Onion: 1/2 small, thinly sliced\", \"7\": \"Lemon Juice: 2 tablespoons\", \"8\": \"Black Pepper: to taste\", \"9\": \"Mixed Greens: 4 cups\", \"10\": \"Tahini Sauce: 3 tablespoons (optional)\", \"11\": \"Fresh Parsley: 1/4 cup, chopped\"}','{\"1\": \"In a large bowl, combine mixed greens, cucumber, tomato, red onion, and parsley.\", \"2\": \"Add cooked falafel pieces on top.\", \"3\": \"In a small bowl, whisk together lemon juice, olive oil, salt, and pepper.\", \"4\": \"Drizzle the dressing over the salad and toss gently.\", \"5\": \"Serve with tahini sauce on the side or drizzled on top.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('160','Sweet Potato Fries','sweet-potato-fries','Crispy baked sweet potato fries with seasoning.','snacklover@example.com','{\"Salt\": \"to taste\", \"Paprika\": \"1 tsp\", \"Olive oil\": \"2 tbsp\", \"Black pepper\": \"to taste\", \"Garlic powder\": \"1/2 tsp\", \"Cornstarch or arrowroot powder\": \"1 tbsp (optional, for extra crispiness)\", \"Optional seasonings (e.g., cayenne, cinnamon, rosemary)\": \"as desired\", \"Sweet potatoes (peeled and cut into thin fries or wedges)\": \"2 large\"}','{\"1\": \"Preheat oven to 220°C (425°F) and line a baking sheet with parchment paper.\", \"2\": \"Place sweet potato fries in a large bowl and toss with cornstarch (if using) to coat lightly.\", \"3\": \"Add olive oil, paprika, garlic powder, salt, pepper, and any optional seasonings. Toss to coat evenly.\", \"4\": \"Spread fries out in a single layer on the baking sheet, making sure they’re not touching.\", \"5\": \"Bake for 15–20 minutes, then flip the fries and bake for another 10–15 minutes until golden and crisp.\", \"6\": \"Remove from oven and let cool for a couple of minutes before serving.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('161','Vegetable Paella','vegetable-paella','Spanish rice dish with mixed vegetables and saffron.','mediterraneancook@example.com','{\"Salt\": \"to taste\", \"Olive oil\": \"3 tbsp\", \"Frozen peas\": \"1 cup\", \"Black pepper\": \"to taste\", \"Lemon wedges\": \"for serving\", \"Smoked paprika\": \"1 tsp\", \"Garlic (minced)\": \"3 cloves\", \"Saffron threads\": \"a pinch\", \"Vegetable broth\": \"4 cups\", \"Tomato (chopped)\": \"2 medium\", \"Onion (finely chopped)\": \"1 medium\", \"Fresh parsley (chopped)\": \"for garnish\", \"Red bell pepper (chopped)\": \"1\", \"Green bell pepper (chopped)\": \"1\", \"Green beans (trimmed and cut)\": \"100 g\", \"Short-grain rice (such as bomba or arborio)\": \"1.5 cups\", \"Artichoke hearts (quartered, canned or frozen)\": \"1 cup\"}','{\"1\": \"Heat olive oil in a large paella pan or wide skillet over medium heat.\", \"2\": \"Add chopped onion and sauté for 5 minutes until translucent. Add garlic and cook for 1 minute.\", \"3\": \"Add chopped red and green bell peppers, and cook for 4–5 minutes until slightly softened.\", \"4\": \"Stir in chopped tomatoes, smoked paprika, and saffron threads. Cook for another 2–3 minutes.\", \"5\": \"Add rice and stir to coat it with the vegetable mixture.\", \"6\": \"Pour in vegetable broth, season with salt and pepper, and bring to a simmer.\", \"7\": \"Add green beans and artichoke hearts. Cook uncovered for 15 minutes without stirring.\", \"8\": \"Sprinkle frozen peas over the top and cook for another 5 minutes, or until rice is tender and liquid is absorbed.\", \"9\": \"Remove from heat and let rest for 5 minutes. Garnish with chopped parsley and lemon wedges before serving.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('162','Chicken Alfredo','chicken-alfredo','Pasta in creamy Alfredo sauce topped with grilled chicken.','italianfoodie@example.com','{\"Salt\": \"to taste\", \"Butter\": \"4 tbsp\", \"Olive oil\": \"2 tbsp\", \"Heavy cream\": \"1 cup\", \"Black pepper\": \"to taste\", \"Garlic (minced)\": \"3 cloves\", \"Fettuccine pasta\": \"300 g (10 oz)\", \"Grated Parmesan cheese\": \"1 cup\", \"Fresh parsley (chopped)\": \"for garnish\", \"Chicken breasts (sliced thin)\": \"2 medium\"}','{\"1\": \"Cook fettuccine pasta according to package instructions until al dente. Drain and set aside.\", \"2\": \"Season chicken slices with salt and pepper.\", \"3\": \"Heat olive oil in a large skillet over medium heat. Cook chicken until golden and cooked through, about 5-7 minutes per side. Remove and set aside.\", \"4\": \"In the same skillet, melt butter and sauté minced garlic for 1-2 minutes until fragrant.\", \"5\": \"Pour in heavy cream and bring to a simmer. Cook for 3-4 minutes, stirring occasionally.\", \"6\": \"Gradually add grated Parmesan cheese, stirring until sauce is smooth and thickened.\", \"7\": \"Return chicken to the skillet and toss to coat in the sauce.\", \"8\": \"Add cooked pasta to the skillet and toss everything together until well combined and heated through.\", \"9\": \"Garnish with chopped fresh parsley and serve immediately.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('163','Tomato Basil Soup','tomato-basil-soup','Smooth tomato soup flavored with fresh basil.','souplover@example.com','{\"Salt\": \"to taste\", \"Olive oil\": \"2 tbsp\", \"Black pepper\": \"to taste\", \"Garlic (minced)\": \"3 cloves\", \"Onion (chopped)\": \"1 medium\", \"Vegetable broth\": \"3 cups\", \"Carrots (chopped)\": \"2 medium\", \"Canned crushed tomatoes\": \"800 g (28 oz, 2 cans)\", \"Fresh basil leaves (chopped)\": \"1/2 cup\", \"Sugar (optional, to balance acidity)\": \"1 tsp\", \"Heavy cream or coconut milk (optional)\": \"1/2 cup\"}','{\"1\": \"Heat olive oil in a large pot over medium heat. Add chopped onion and carrots; sauté for 5-7 minutes until softened.\", \"2\": \"Add minced garlic and cook for 1 minute until fragrant.\", \"3\": \"Pour in crushed tomatoes and vegetable broth. Bring to a boil, then reduce heat and simmer for 20 minutes.\", \"4\": \"Add chopped fresh basil, salt, pepper, and sugar if using. Simmer for another 5 minutes.\", \"5\": \"Use an immersion blender or transfer to a blender to puree the soup until smooth.\", \"6\": \"Stir in heavy cream or coconut milk if desired. Heat through but do not boil.\", \"7\": \"Serve hot, garnished with extra basil or a drizzle of cream.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('164','Cinnamon Rolls','cinnamon-rolls','Soft, sweet rolls with cinnamon and cream cheese frosting.','dessertqueen@example.com','{\"Eggs\": \"2 large\", \"Salt\": \"1 tsp\", \"Active dry yeast\": \"2 1/4 tsp (1 packet)\", \"Granulated sugar\": \"1/2 cup\", \"All-purpose flour\": \"4 cups\", \"Whole milk (warm)\": \"1 cup\", \"Unsalted butter (melted)\": \"1/3 cup\", \"Brown sugar (for filling)\": \"1 cup\", \"Milk or cream (for icing)\": \"2-3 tbsp\", \"Powdered sugar (for icing)\": \"1 1/2 cups\", \"Vanilla extract (for icing)\": \"1 tsp\", \"Ground cinnamon (for filling)\": \"2 tbsp\", \"Unsalted butter (softened, for filling)\": \"1/2 cup\"}','{\"1\": \"In a large bowl, mix warm milk, yeast, and 1 tablespoon sugar. Let sit for 5-10 minutes until foamy.\", \"2\": \"Add melted butter, eggs, remaining sugar, salt, and flour to the yeast mixture. Mix until a dough forms.\", \"3\": \"Knead the dough on a floured surface for about 8-10 minutes until smooth and elastic.\", \"4\": \"Place dough in a greased bowl, cover, and let rise in a warm place for about 1-1.5 hours, or until doubled in size.\", \"5\": \"Roll out dough into a 16x12 inch rectangle on a floured surface.\", \"6\": \"Spread softened butter over the dough. Mix brown sugar and cinnamon, then sprinkle evenly over the butter.\", \"7\": \"Roll up the dough tightly from the long side and cut into 12 equal slices.\", \"8\": \"Place slices in a greased baking dish. Cover and let rise for another 30-45 minutes.\", \"9\": \"Preheat oven to 180°C (350°F). Bake rolls for 25-30 minutes until golden brown.\", \"10\": \"Mix powdered sugar, vanilla, and milk to make icing. Drizzle over warm rolls before serving.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('165','Beef Tacos','beef-tacos','Seasoned beef served in tortillas with fresh toppings.','tacotime@example.com','{\"Salt\": \"to taste\", \"Paprika\": \"1 tsp\", \"Olive oil\": \"1 tbsp\", \"Sour cream\": \"for serving\", \"Ground beef\": \"500 g (1 lb)\", \"Lime wedges\": \"for serving\", \"Black pepper\": \"to taste\", \"Chili powder\": \"1 tbsp\", \"Ground cumin\": \"1 tsp\", \"Diced tomatoes\": \"1 cup\", \"Garlic (minced)\": \"2 cloves\", \"Onion (chopped)\": \"1 small\", \"Shredded lettuce\": \"1 cup\", \"Shredded cheddar cheese\": \"1 cup\", \"Fresh cilantro (chopped)\": \"for garnish\", \"Taco shells or tortillas\": \"8-10\"}','{\"1\": \"Heat olive oil in a skillet over medium heat. Sauté chopped onion until soft, about 3-4 minutes.\", \"2\": \"Add minced garlic and cook for 1 minute until fragrant.\", \"3\": \"Add ground beef and cook until browned, breaking it apart with a spoon.\", \"4\": \"Stir in chili powder, cumin, paprika, salt, and pepper. Cook for another 2-3 minutes.\", \"5\": \"Warm taco shells or tortillas according to package instructions.\", \"6\": \"Assemble tacos by filling shells with beef mixture, then topping with lettuce, tomatoes, cheese, sour cream, and cilantro.\", \"7\": \"Serve with lime wedges on the side.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('166','Cauliflower Buffalo Bites','cauliflower-buffalo-bites','Baked cauliflower tossed in spicy buffalo sauce.','veganchef@example.com','{\"Salt\": \"1/2 tsp\", \"Water\": \"3/4 cup\", \"Paprika\": \"1/2 tsp\", \"Onion powder\": \"1/2 tsp\", \"Buffalo sauce\": \"1/2 cup\", \"Garlic powder\": \"1 tsp\", \"All-purpose flour\": \"3/4 cup\", \"Cauliflower florets\": \"1 medium head (about 4 cups)\", \"Olive oil or melted butter\": \"2 tbsp\"}','{\"1\": \"Preheat oven to 220°C (425°F). Line a baking sheet with parchment paper.\", \"2\": \"In a large bowl, whisk together flour, water, garlic powder, onion powder, salt, and paprika to make a batter.\", \"3\": \"Dip cauliflower florets into the batter, coating each piece well. Shake off excess batter.\", \"4\": \"Place coated florets on the baking sheet in a single layer.\", \"5\": \"Bake for 20 minutes, flipping halfway through, until crispy and golden.\", \"6\": \"In a small bowl, mix buffalo sauce with olive oil or melted butter.\", \"7\": \"Remove cauliflower from oven and toss with the buffalo sauce mixture until evenly coated.\", \"8\": \"Return to oven and bake for an additional 10 minutes.\", \"9\": \"Serve hot with ranch or blue cheese dressing.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('167','Lemon Garlic Salmon','lemon-garlic-salmon','Oven-baked salmon with lemon and garlic seasoning.','seafoodfan@example.com','{\"Salt\": \"to taste\", \"Olive oil\": \"2 tbsp\", \"Lemon zest\": \"1 tsp\", \"Lemon juice\": \"2 tbsp\", \"Black pepper\": \"to taste\", \"Salmon fillets\": \"4 pieces (about 150-200 g each)\", \"Garlic (minced)\": \"3 cloves\", \"Fresh parsley (chopped)\": \"for garnish\"}','{\"1\": \"Preheat oven to 200°C (400°F). Line a baking sheet with parchment paper.\", \"2\": \"In a small bowl, mix olive oil, minced garlic, lemon juice, lemon zest, salt, and pepper.\", \"3\": \"Place salmon fillets on the baking sheet skin-side down. Brush the lemon garlic mixture evenly over the fillets.\", \"4\": \"Bake for 12-15 minutes, or until the salmon flakes easily with a fork.\", \"5\": \"Garnish with chopped fresh parsley before serving.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('168','Minestrone Soup','minestrone-soup','Hearty vegetable soup with beans and pasta.','souplady@example.com','{\"Salt\": \"to taste\", \"Olive oil\": \"2 tbsp\", \"Dried basil\": \"1 tsp\", \"Black pepper\": \"to taste\", \"Dried oregano\": \"1 tsp\", \"Carrots (diced)\": \"2 medium\", \"Garlic (minced)\": \"3 cloves\", \"Onion (chopped)\": \"1 medium\", \"Vegetable broth\": \"4 cups\", \"Zucchini (diced)\": \"1 medium\", \"Canned diced tomatoes\": \"1 can (400 g / 14 oz)\", \"Celery stalks (diced)\": \"2\", \"Fresh spinach or kale (chopped)\": \"2 cups\", \"Grated Parmesan cheese (optional)\": \"for serving\", \"Cannellini beans (drained and rinsed)\": \"1 can (400 g / 14 oz)\", \"Small pasta (like ditalini or elbow macaroni)\": \"1 cup\"}','{\"1\": \"Heat olive oil in a large pot over medium heat. Add onion, carrots, and celery; sauté for 5-7 minutes until softened.\", \"2\": \"Add minced garlic and cook for 1 minute until fragrant.\", \"3\": \"Stir in zucchini, diced tomatoes, cannellini beans, vegetable broth, oregano, and basil. Bring to a boil.\", \"4\": \"Add pasta and reduce heat to a simmer. Cook until pasta is tender, about 10 minutes.\", \"5\": \"Stir in fresh spinach or kale and cook for another 2-3 minutes until wilted.\", \"6\": \"Season with salt and black pepper to taste.\", \"7\": \"Serve hot, topped with grated Parmesan if desired.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('169','Chocolate Chip Cookies','chocolate-chip-cookies','Classic chewy cookies loaded with chocolate chips.','dessertlover@example.com','{\"Eggs\": \"2 large\", \"Salt\": \"1/2 tsp\", \"Baking soda\": \"1 tsp\", \"Vanilla extract\": \"1 tsp\", \"Granulated sugar\": \"3/4 cup\", \"All-purpose flour\": \"2 1/4 cups\", \"Brown sugar (packed)\": \"3/4 cup\", \"Semi-sweet chocolate chips\": \"2 cups\", \"Unsalted butter (softened)\": \"1 cup (2 sticks)\"}','{\"1\": \"Preheat oven to 190°C (375°F). Line baking sheets with parchment paper.\", \"2\": \"In a medium bowl, whisk together flour, baking soda, and salt.\", \"3\": \"In a large bowl, beat softened butter, granulated sugar, brown sugar, and vanilla extract until creamy.\", \"4\": \"Add eggs one at a time, beating well after each addition.\", \"5\": \"Gradually add flour mixture to the butter mixture, mixing until just combined.\", \"6\": \"Stir in chocolate chips.\", \"7\": \"Drop rounded tablespoons of dough onto prepared baking sheets about 2 inches apart.\", \"8\": \"Bake for 9-11 minutes or until edges are golden but centers are still soft.\", \"9\": \"Cool on baking sheets for 2 minutes, then transfer to wire racks to cool completely.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('170','Chicken Satay','chicken-satay','Grilled chicken skewers served with peanut sauce.','asianflavor@example.com','{\"Soy sauce\": \"3 tbsp\", \"Lime juice\": \"2 tbsp\", \"Brown sugar\": \"2 tbsp\", \"Coconut milk\": \"1/4 cup\", \"Peanut butter\": \"1/2 cup\", \"Garlic (minced)\": \"3 cloves\", \"Ground turmeric\": \"1 tsp\", \"Ground coriander\": \"1 tsp\", \"Water (for peanut sauce)\": \"1/4 cup\", \"Soy sauce (for peanut sauce)\": \"2 tbsp\", \"Lime juice (for peanut sauce)\": \"1 tbsp\", \"Brown sugar (for peanut sauce)\": \"1 tbsp\", \"Chicken breasts (cut into strips)\": \"500 g (1 lb)\", \"Garlic (minced, for peanut sauce)\": \"1 clove\", \"Chili flakes (optional, for peanut sauce)\": \"to taste\"}','{\"1\": \"In a bowl, mix soy sauce, brown sugar, lime juice, minced garlic, ground coriander, ground turmeric, and coconut milk.\", \"2\": \"Add chicken strips to the marinade and refrigerate for at least 1 hour.\", \"3\": \"Thread marinated chicken onto skewers.\", \"4\": \"Preheat grill or grill pan to medium-high heat. Grill chicken skewers for 4-5 minutes per side until cooked through.\", \"5\": \"To make peanut sauce, combine peanut butter, soy sauce, brown sugar, lime juice, minced garlic, water, and chili flakes in a small saucepan over low heat.\", \"6\": \"Stir until smooth and heated through, adding water as needed for desired consistency.\", \"7\": \"Serve chicken satay with peanut sauce for dipping.\"}',NULL,NULL);
INSERT INTO `` (`id`,`name`,`slug`,`description`,`author_email`,`ingredients`,`recipe_steps`,`created_at`,`updated_at`) VALUES ('179','Thai Green Curry','thai-green-curry','Spicy and creamy curry with vegetables and coconut milk.','thaichef@example.com','{\"1\": \"Fish sauce: 2 tbsp\", \"2\": \"Coconut milk: 400 ml\", \"3\": \"Vegetable oil: 2 tbsp\", \"4\": \"Green curry paste: 2-3 tbsp (adjust to taste)\", \"5\": \"Fresh basil leaves: 1 handful\", \"6\": \"Cooked jasmine rice: to serve\", \"7\": \"Bamboo shoots (drained): 100 g\", \"8\": \"Kaffir lime leaves (torn): 3-4 leaves\", \"9\": \"Palm sugar or brown sugar: 1 tbsp\", \"10\": \"Red chili (sliced, for garnish): 1\", \"11\": \"Chicken breast or thighs (sliced): 500 g\", \"12\": \"Thai eggplant or baby eggplants (quartered): 100 g\"}','{\"1\": \"Heat the vegetable oil in a large saucepan or wok over medium heat.\", \"2\": \"Add the green curry paste and fry for 1-2 minutes until fragrant.\", \"3\": \"Gradually stir in half of the coconut milk and bring to a gentle simmer.\", \"4\": \"Add the sliced chicken and cook for 5-7 minutes until the chicken is nearly cooked through.\", \"5\": \"Stir in the remaining coconut milk, eggplants, and bamboo shoots.\", \"6\": \"Add fish sauce, palm sugar, and kaffir lime leaves. Simmer for another 5-10 minutes until vegetables are tender.\", \"7\": \"Taste and adjust seasoning with more fish sauce or sugar if needed.\", \"8\": \"Stir in fresh basil leaves and cook for 1 more minute.\", \"9\": \"Serve hot, garnished with sliced red chili, over jasmine rice.\"}',NULL,NULL);
